			);

SELECT SELF
			VARIABLE HIGHER_COUNTER = 0;
			FORALL TOWER
			(
				IF ((GET DISTANCE SELF SELECTION) <= 45.3)
				(
					IF (SELECTION != SELF)
					(
						IF ((GET TOWER_LEVEL SELF) > (GET TOWER_LEVEL SELECTION))
						(
							HIGHER_COUNTER = (HIGHER_COUNTER + 1);
						)
						ELSE
						(
							HIGHER_COUNTER = 0;
							STOP;
						);
					);
				);
			);
			IF (HIGHER_COUNTER > 0)
			(
				MOD SELF TOWER_DAMAGE_BONUS 50 PERCENT;
			);

