			(
				DELETE SELF;
			);

TRIGGER WORLDGAME ONCLICK
			(
				BUILD_TOWER SELF (GET COORDS MOUSE);
				FORALL TOWER
				(
					IF ((GET INTERSECT_POINT SELF (GET POSITION MOUSE)) == TRUE)
					(
						FORGET_SPELL 1;
						FORGET_SPELL 2;
						FORGET_SPELL 3;
						FORGET_SPELL 422;
						FORGET_SPELL 418;
						FORGET_SPELL 355;
						FORGET_SPELL 339;
						FORGET_SPELL 340;
						FORGET_SPELL 345;
						FORGET_SPELL 349;
						FORGET_SPELL 352;
						FORGET_SPELL 426;
						APPLY_SPELL 423 SELF SELF;
						APPLY_SPELL 424 SELF SELF;
						APPLY_SPELL 425 SELF SELF;
						REFRESH SELF;
						STOP;
					);
				);
			);

